import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
//import 'font-awesome/css/font-awesome.min.css';
import './App.css';
import Header from '../header/header';
import Footer from '../footer/footer';
import Content from '../content/content';
import auth from '../authentication/auth';
import * as actions from '../../reduxState/actions';

class App extends Component {

  newElement = (tag) => {
    if (tag === 'none') {
      return;

    } else if (tag !== 'video' || 'img') {
      let newE = React.createElement(tag, { id: id, className: 'web-element', style: { width: '100%' }, onClick: this.addToStyling }, 'New Element');
    } else {
      let newE = React.createElement(tag, { id: id, className: 'web-element', style: { width: '100%' }, onClick: this.addToStyling, src: 'C://test.kkkkkk' });
    }

    let id = Math.random();

    /*Each element will have its own hidden texarea hidden below it 
    and it will be wrapped around a div with className: new-element-wrapper*/
    let newEWithEditBox =
      <div className='new-element-wrapper' id={'wrapper:' + id}>
        {newE}
        <div style={{ display: 'none', width: '100%' }} id={`edit-box:${id}`} className='temporary-elements'>
          <textarea id={`text-area:${id}`} className='web-text-area'></textarea>
          <button className='btn btn-primary btn-sm' onClick={(e) => this.saveNewText(id)}>Save</button>
        </div>
        <button className='badge btn-outline-secondary'>
          <span className='fa fa-edit fa-lg temporary-elements' onClick={(e) => this.editText(id)}></span>
        </button>
        <button className='badge btn-outline-danger'>
          <span className='fa fa-trash fa-lg temporary-elements' onClick={(e) => this.beforeElementDeletion(id)}></span>
        </button>
      </div>;

    actions.createWebElement(newEWithEditBox);

    document.querySelector('#new_element').value = 'none';
  }

  beforeElementDeletion = (id) => {
    //to avoid some bugs before deleting an element, we need to ensure that text-area is hidden
    document.getElementById('text-area:' + id).style.display !== 'none' && this.saveNewText(id);
    actions.deleteWebElement('wrapper:' + id);
  }

  addToStyling = (e) => {
    document.getElementById('id').value = e.target.id;
    Array.from(document.getElementsByClassName('new-element-wrapper')).forEach(element =>
      element.id === `wrapper:${e.target.id}` ? element.style.background = 'rgba(154, 218, 218, 0.829)' : element.style.background = 'white')
  }

  editText = (e) => {
    document.getElementById('edit-box:' + e).style.display = 'block';
    let elementToUpdate = document.getElementById(e);

    document.getElementById('text-area:' + e).value = elementToUpdate.innerHTML;
    elementToUpdate.style.display = 'none';
  }
  saveNewText = (id) => {
    document.getElementById(id).style.display = 'block';
    actions.updateWebElement({ id: 'wrapper:' + id, text: document.getElementById('text-area:' + id).value });
    document.getElementById('edit-box:' + id).style.display = 'none';
  }
  componentDidMount() {
    if (!(localStorage.getItem('authorization')))
      this.props.history.push('/login')
  }

  render() {
    return (
      <div className='main-container' id='main-container'>
        <Header
          onWebPreview={() => this.webPreview()}
          onLogout={() => auth.logout(() => this.props.history.push('/'))}
          onNewElem={() => this.newElement(document.querySelector('#new_element').value)} />
        <Content />
        <Footer />
      </div>
    );
  }
}

export default App;
