import React, { Component } from 'react';
import { setStyle } from './content.controller';

class StyleComponent extends Component {
    state = {}
    render() {
        if (this.props.isDropDownMenu === 'true') {
            return (
                <div className='property'>
                    <select id={this.props.name} onChange={() => setStyle(this.props.name, this.props.unit)}>
                        {this.props.items.map(item => <option value={item} key={item}>{item}</option>)}
                    </select>
                    {this.props.text}
                </div>
            )
        }
        return (
            <div className='property'>
                <input type={this.props.type} id={this.props.name} className='style-input' 
                onChange={() => setStyle(this.props.name, this.props.unit)}/>
                <span>{this.props.text}</span>
            </div>
        );
    }
}

export default StyleComponent;
