import React, { Component } from 'react';
import './App.css';
/*import axios from 'axios';  */
import Home from '../home/home'
import Messages from '../messages/messages'
//import http from '../http.service/http.service.js';

class App extends Component { 

  state = {
    routes: { 
      home: <Home/>,
      messages: <Messages/>
    },
    currentRoute: <Home onLogin={this.login}/>,
    routeTitle: '',
    user: []
  }
  router = (route, comp) => {
    window.history.pushState('data', 'home page', route)
    this.setState({ currentRoute: comp })
    this.setState({ routeTitle: 'title' })
  }
  constructor(){  }

  login(){ 
    alert('');
    let username = document.getElementById('username').value;
    let password = document.getElementById('password').value;
    this.http("POST", 'http://localhost:8080/chat-app-2/login.php', {username: username, password: password}, (e) => { 
      return e;
    })
    }

  render(){
    this.state.routeTitle === '' && window.history.pushState('data2', 'index', '/')

    return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to React Js : {this.state.user}</h1>
        <span onClick={()=> this.router('home', this.state.routes.home)}>home
        </span>
        <span onClick={()=> this.router('messages', this.state.routes.messages)}>messages</span>
      </header>
      <div id="content">{this.state.currentRoute}</div>
    </div>
    )
  }
}

export default App;
