const reducer = (state = [], action) =>{
    if(action.type === 'UPDATE_NAME')
        return [{name: action.payload}];
    if(action.type === 'DELETE_NAME')
        return state.filter(item => item.name !== action.payload);
  return [{name: 'phillip'}]
};


export default reducer;
