import React, { Component } from 'react';
import './App.css';
import store from '../../reduxState/store';


class App extends Component {
  changeState = (name) => {
    store.dispatch({ type: 'UPDATE_NAME', payload: name });
  }
  deleteName = (name) => {
    store.dispatch({ type: 'DELETE_NAME', payload: name });
  }
  addName = (name) => {
    store.dispatch({ type: 'ADD_NAME', payload: name });
  }
  render() {
    console.log(store.getState())
    return (
      <div className="App">
        <h1>App Component</h1>
        {store.getState().map(store =>
          <div>
            <span key={store.name}>{store.name}</span>
            <button onClick={() => this.deleteName(store.name)}>Delete Name</button>
          </div>
        )}
        <button onClick={() => this.changeState('phillip')}>Change state</button>
        <button onClick={() => this.changeState('test name two')}>Change state</button>
        <button onClick={() => this.addName('new added name')}>Add Name</button>
        <h1>TEST</h1>
      </div>
    )
  }
}

export default App;
