import React, { Component } from 'react';
import './App.css';
import store from '../../reduxState/store';


class App extends Component { 
  changeState = (name) =>{ 
    store.dispatch({type: 'UPDATE_NAME', payload: name});
  }
  changeState2 = () =>{ 
    store.dispatch({type: 'UPDATE_NAME', payload: 'phillip'});
  }
  render(){
    console.log(store.getState())
    return (
    <div className="App">
      <h1>App Component</h1>
      <p>{store.getState().name}</p>
      <button onClick={() => this.changeState('test name')}>Change state</button>
      <button onClick={this.changeState2}>Change state</button>
    </div>
    )
  }
}

export default App;
