import http from '../http/http';

class Auth {
    constructor() {
        this.isAuthenticated = false;
        this.login = (username, password) => {
            fetch(`http://localhost/login.json`, {
                method: 'POST',
                body: JSON.stringify({
                    username,
                    password
                })
            });
            console.log('cannot request');
        }
        this.logout = () => {

        }
    }
}

export default new Auth();
