import React, { Component } from 'react';
import StyleComponent from './style.component'
import store from '../../reduxState/store';

class Content extends Component {
    componentDidMount() {
        //for responsive design
        if (window.screen.width < 400) {
            document.getElementById('content').style['flex-wrap'] = 'wrap';
            document.getElementById('content').style['flex-direction'] = 'column';
        }

    }
    render() {
        return (
            <div className='content' id='content'>
                <div className='styling' id='styling'>
                    <div style={{ textAlign: 'center' }} className='style-heading'><h2>Style</h2></div>
                    <StyleComponent name='id' type='hidden' text='ID' unit='' />
                    <StyleComponent name='backgroundColor' type='color' text='Background Color' unit='' />
                    <StyleComponent name='borderColor' type='color' text='Border Color' unit='' />
                    <StyleComponent name='borderRadius' type='number' text='Border Radius' unit='px' />
                    <StyleComponent name='borderStyle' isDropDownMenu='true' items={['solid', 'dotted']} type='text' text='Border Style' unit='' />
                    <StyleComponent name='borderWidth' type='number' text='Border Width' unit='px' />
                    <StyleComponent name='color' type='color' text='Color' unit='' />
                    <StyleComponent name='fontSize' type='number' text='Font Size' unit='px' />
                    <div className='property'>
                        <details>
                            <summary>margin</summary>
                            <StyleComponent name='marginTop' type='number' text='Margin Top' unit='px' />
                            <StyleComponent name='marginBottom' type='number' text='Margin bottom' unit='px' />
                            <StyleComponent name='marginLeft' type='number' text='Margin Left' unit='px' />
                            <StyleComponent name='marginRight' type='number' text='Margin Right' unit='px' />
                        </details>
                    </div>
                    <div className='property'>
                        <details>
                            <summary>Padding</summary>
                            <StyleComponent name='paddingTop' type='number' text='Padding Top' unit='px' />
                            <StyleComponent name='paddingBottom' type='number' text='Padding Bottom' unit='px' />
                            <StyleComponent name='paddingLeft' type='number' text='Padding Left' unit='px' />
                            <StyleComponent name='paddingRight' type='number' text='Padding Right' unit='px' />
                        </details>
                    </div>
                    <StyleComponent name='textAlign' isDropDownMenu='true' items={['left', 'center', 'right', 'justify']} type='text' text='Text Align' unit='' />
                    <br />
                </div>
                <div id='web'>{store.getState().web.map((el, i) =>
                    <React.Fragment key={i}>{el.state}</React.Fragment>)}
                </div>
            </div>
        );
    }
}

export default Content;
