import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import 'font-awesome/css/font-awesome.min.css';
import './header.css';

class Header extends Component {
    render() {
        if (window.screen.width < 400) {
            return (
                <div className='fa fa-menu'>Test</div>
            )
        }
        return (
            <div>
                <select onChange={this.props.onNewElem} id='new_element' className='selectElement'>
                    <option value='none'>Add Text Element</option>
                    <option value='h1'>Heading</option>
                    <option value='p'>Paragraph</option>
                    <option value='a'>Link</option>
                </select>
                <select onChange={this.props.onNewElem} id='new_element' className='selectElement'>
                    <option value='none'>Add Image/Video</option>
                    <option value='video' >Video</option>
                    <option value='img' >Image</option>
                </select>
                <button className='selectElement'><span className='fa fa-bug'></span>Report bugs</button>
            </div>
        );
    }
}

export default Header;
