import * as actions from '../../reduxState/actions';

export function setStyle(e, unit) {
    let id = `wrapper:${document.getElementById('id').value}`;

    if (document.getElementById(id) === null) {
        alert('please click on the HTML element to style it !!');
        return;
    }

    let styleProp = e;
    let styleValue = document.getElementById(e).value;

    let payload = {
        id,
        styleProp,
        styleValue,
        unit
    }

    actions.updateStyling(payload);
}
