import { produce } from 'immer';
import * as actionType from '../action.types';

const initialState = []

export const web = (state = initialState, action) => {
    switch (action.type) {

        case actionType.UPDATE_WEB_ELEMENT:
            return state.map(el =>
                el.state.props.id === action.payload.id ?
                    produce(el, updated => {
                        updated.state.props.children[0].props.children[0].innerHTML = action.payload.text;
                    })
                    : el
            )
        case actionType.UPDATE_STYLING:
            return state.map(el =>
                el.state.props.id === action.payload.id ?
                    produce(el, updated => {
                        updated.state.props.children[0].props.style[action.payload.styleProp] = `${action.payload.styleValue}${action.payload.unit}`;
                    })
                    : el
            )
        case actionType.DELETE_WEB_ELEMENT:
            return state.filter(el => el.state.props.id !== action.payload);

        case actionType.CREATE_WEB_ELEMENT:
            return [...state, { state: action.payload }];

        default:
            return state
    }
};

export default web;
