import * as actionType from '../action.types';

const initialState = []

export const userReducer = (user = initialState, action) => {
    switch (action.type) {
        case actionType.UPDATE_USER:
            return [{ user: action.payload }];

        case actionType.DELETE_USER:
            return [];
        default:
            return [user]
    }
};

export default userReducer;
