const reducer = (state, actions) =>{
    if(actions.type === 'UPDATE_NAME')
        return {name: actions.payload};
    if(actions.type === 'DELETE_NAME')
        return state.filter(item => item.name !== actions.payload.name);
  return {name: 'phillip'}
};


export default reducer;
