import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import 'font-awesome/css/font-awesome.min.css';
import './navigation.css';

import auth from '../authentication/auth';
import store from '../../reduxState/store';

//import axios from 'axios';

class Navigation extends Component {

    navigate = (route) => {
        this.props.history.push(route);
    }

    signOut = () => {
        localStorage.removeItem('authorization');
        this.navigate('login');
    }

    saveWeb = () => {
        auth.save(localStorage.getItem('authorization'))
            .then(res => console.log(res.data));

        this.props.history.push('myWebsite');
    }

    responsiveDesign = () => {
        let width = window.screen.width;
        if (width < 550) {
            document.getElementById('header').style.flexDirection = 'column-reverse';
            Array.from(document.getElementById('header').children).forEach(child =>
                child.style.width = '100%')
            console.log('width grteater than 550px')
        }
        else {
            document.getElementById('header').style.flexDirection = 'row';
            Array.from(document.getElementById('header').children).forEach(child =>
                child.style.width = '400px')
            console.log('width grteater than 550px')
        }
    }
    componentDidMount() {
        this.responsiveDesign();
        document.body.onresize = () => this.responsiveDesign();
    }

    render() {
        return (
            <div className='header' id='header'>
                <div className='navbar'>
                    <div className='btn btn-info btn-sm' onClick={() => this.navigate('/preview')}>
                        <span className='fa fa-eye fa-md'></span>Preview
                    </div>
                    <div className='btn btn-primary btn-sm' onClick={() => this.navigate('/')}>
                        <span className='fa fa-eye-slash fa-md'></span>Workspace
                    </div>
                    <div className='btn btn-success btn-sm' onClick={this.saveWeb}>
                        <span className='fa fa-save fa-md'></span>Save
                    </div>
                </div>
                <div className='sign-out-link'>
                    <span className='greeting'>
                        <span className="user">Logged in as <span>{store.getState().user.user}</span></span>
                    </span>
                    <span className='sign-out'>
                        <div className='btn btn-secondary btn-sm' onClick={this.signOut}>
                            <span className='fa fa-sign-out fa-md '></span>Sign out
                        </div>
                    </span>
                </div>
            </div>
        );
    }
}

export default Navigation;
