import React, { Component } from 'react';
import Intro from '../intro';
import './App.css';
import axios from 'axios';
import { Link } from 'react-router';

const api = axios.create({ 
  baseUrl: `http://localhost:8080`
})

class App extends Component { 

  state = { 
    users : []
  }
  props = { 
    title: 'title',
    tags: 'p'
  }

login = async () => { 
  let form = new FormData();
  form.append('email', 'peter@gmail.com');
  form.append('password', '111');
  let res = await api.post('http://localhost:8080/logistic-service/customers_login.php', form)
    document.write(res.data)
    //this.setState({users: res.data})
}

getUser(arg){ 
  console.log(arg)
}

  render(){
    return (
    <div className="App">
      <header className="App-header">
      {this.state.users.map(user => <h4 key={user.id} onClick={()=> this.getUser(user.id)}> {user.name} </h4>)}
        <h1>Welcome to React Js</h1>
        <h2>React is nice but it is slow</h2>
        <Intro message = "Here you will find all your favorite series" />
      </header>
      <button onClick={this.login}>fetch data</button>

      <Link to={`posts/${props.postId}`}>
      <div className="post-button" >
      {props.title}
      <span>{props.tags}</span>
      </div>
      </Link>
    </div>

    )
  }
}

export default App;
