import * as actionType from '../action.types';

const initialState = []

export const reducer = (webPreview = initialState, action) => {
    switch (action.type) {
        case actionType.UPDATE_WEB_PREVIEW:
            return [{ webPreview: action.payload }];

        case actionType.DELETE_WEB_PREVIEW:
            return webPreview.filter(item => item.element !== action.payload);

        case actionType.CREATE_WEB_PREVIEW:
            return [...webPreview, { webPreview: action.payload }];

        default:
            return [{ webPreview }]
    }
};

export default reducer;
