export default function responsiveDesign() {
    let width = window.screen.width;

    //conditionally rendering components based on screen width
    if (width < 842) {
        document.getElementById('content').style['flex-wrap'] = 'wrap';
        document.getElementById('content').style['flex-direction'] = 'column';
        document.getElementById('content').style['align-content'] = 'center';
        document.getElementById('content').style['justify-content'] = 'space-around';

        document.getElementById('header').style.flexDirection = 'column-reverse';
        Array.from(document.getElementById('header').children).forEach(child =>
            child.style.width = '100%')

    }
    else {
        document.getElementById('content').style['flex-wrap'] = 'nowrap';
        document.getElementById('content').style['flex-direction'] = 'row';

        document.getElementById('header').style.flexDirection = 'row';
        Array.from(document.getElementById('header').children).forEach(child =>
            child.style.width = '400px')
    }
}
