import React, { Component } from 'react';

import auth from '../authentication/auth';
import * as actions from '../../reduxState/actions';


class LandingPage extends Component {
    state = {
        username: null,
        password: null,
        msgFromServer: null
    }

    onLogin = () => {
        auth.login(this.state.username, this.state.password)
            .then(res => {
                if (res.data === 'password incorrect')
                    this.setState({ msgFromServer: res.data })
                else {
                    localStorage.setItem('authorization', 'bearer ' + res.data.accessToken);
                    actions.updateUser(res.data.username);
                    this.props.history.push('/');
                }
            })
    }

    componentDidMount() {
        if (localStorage.getItem('authorization'))
            this.props.history.push('/')
    }

    myFunc() {
        button.addEventListener('click', function () {
            event.preventDefault();
            socket.emit('chat message', username, textbox.value);
        });

        socket.on('server to client', function (name, msg) {
            var newMsg = document.createElement('li');
            newMsg.innerHTML = name + ':' + msg;
            //del.innerHTML = '<input type="button">';
            messages.appendChild(newMsg);
            textbox.value = '';
        });
    }

    render() {
        return (
            <div>
                <h2>Landing Page</h2>
                <p>{this.state.msgFromServer}</p>
                <input type='text' onChange={(e) => this.setState({ username: e.target.value })} name='username' placeholder='User Name' />
                <br />
                <input type='password' onChange={(e) => this.setState({ password: e.target.value })} name='username' placeholder='Password' />
                <br />
                <button onClick={this.onLogin}>Login</button>
                <button onClick={this.myFunc}>Send Msg</button>
            </div>
        );
    }
}

export default LandingPage;
