import React, { Component } from 'react';
import auth from '../authentication/auth'

class LandingPage extends Component {
    state = {
        username: null,
        password: null,
        msgFromServer: null
    }

    onLogin = () => {
        auth.login(this.state.username, this.state.password)
            .then(res => {
                if (res.data === 'password incorrect')
                    this.setState({ msgFromServer: res.data })
                else {
                    localStorage.setItem('authorization', 'bearer ' + res.data.accessToken)
                    this.props.history.push('/home');
                }
            })
    }

    componentWillMount() {
        if (!(localStorage.getItem('authorization')))
            this.props.history.push('/home')
    }

    render() {
        return (
            <div>
                <h2>Landing Page</h2>
                {this.state.msgFromServer}
                <input type='text' onChange={(e) => this.setState({ username: e.target.value })} name='username' placeholder='User Name' />
                <input type='password' onChange={(e) => this.setState({ password: e.target.value })} name='username' placeholder='Password' />
                <button onClick={this.onLogin}>Login</button>
            </div>
        );
    }
}

export default LandingPage;
