import React, { Component } from 'react';
import './App.css';
/*import axios from 'axios';  */
import Home from '../home/home'
import Messages from '../messages/messages'

class App extends Component { 

  state = {
    routes: [ 
      { home: <Home/>},
      { messages: <Messages/>}]
  }
  router = (arg, comp) => {
    window.history.pushState('data', 'home page', arg)
    //document.getElementById('content')
  }
  render(){
    return (
    <div className="App">
      <header className="App-header">
        <h1>Welcome to React Js</h1>
        <span onClick={()=> this.router('home')}>home</span>
        <span onClick={()=> this.router('messages')}>messages</span>
      </header>
      <div id="content"></div>
    </div>
    )
  }
}

export default App;
