import React, { Component } from 'react';
import Intro from '../intro';
import './App.css';
/*import axios from 'axios';  */

class App extends Component { 


  render(){
    let state = { 
      state1: 'first state',
      users: []
    }
    return (
    <div className="App">
      <header className="App-header">
      {/*this.state.users.map(user => <h4 key={user.id} onClick={()=> this.getUser(user.id)}> {user.name} </h4>) */}
        <h1>Welcome to React Js</h1>
        <h2>React is nice but it is slow</h2>
        <Intro message = "Here you will find all your favorite series" />
      </header>
      <button onClick={(e) => {state.state1  = "new state"; e.target.innerHTML=state.state1}}>{state.state1}</button>
      <div onClick={(e) => this.login('path2')}>fetch data</div>
      <div onClick={(e) => this.login('testpath')}>fetch data</div>
    </div>
    )
  }
}

export default App;
