import Intro from '../intro';
import './App.css';
import axios from 'axios';

const api = axios.create({ 
  baseUrl: `http://localhost:8080`
})

class App extends Component { 

  state = { 
    users : []
  }

   getFormData($form) {
    var unindexed_array = $form.serializeArray();
    var formdata = new FormData();
    $form.map(unindexed_array, (n, i) => { 
      formdata.append(n['email'], n['peter@gmail.com']);
    });
    return formdata;
  }

login = async () => { 
  let res = await api.post('http://localhost:8080/logistic-service/customers_login.php', getFormData("#cad_evolucao"))
