import React, { Component } from 'react';
import './App.css';
import store from '../../reduxState/store';


class App extends Component { 
  changeState = () =>{ 
    store.getState().name = 'phillip' ? store.dispatch({type: 'UPDATE_NAME', payload: 'tonic'}):
    store.dispatch({type: 'UPDATE_NAME', payload: 'phillip'});
  }
  render(){
    console.log(store.getState())
    return (
    <div className="App">
      <h1>App Component</h1>
      <p>{store.getState().name}</p>
      <button onClick={this.changeState}>Change state</button>
      <button onClick={this.changeState}>Change state</button>
    </div>
    )
  }
}

export default App;
