import React, { Component } from 'react';
import auth from '../authentication/auth'

class LandingPage extends Component {
    state = {
        username: null,
        password: null
    }

    onLogin = () => {
        let res = auth.login(this.state.username, this.state.password)

        localStorage.setItem('authorization', 'bearer ' + res.data.accessToken)

        this.props.history.push('/home');
    }

    render() {
        return (
            <div>
                <h2>Landing Page</h2>
                <input type='text' onChange={(e) => this.setState({ username: e.target.value })} name='username' placeholder='User Name' />
                <input type='password' onChange={(e) => this.setState({ password: e.target.value })} name='username' placeholder='Password' />
                <button onClick={this.onLogin}>Login</button>
            </div>
        );
    }
}

export default LandingPage;
