import axios from 'axios';

class Auth {
    constructor() {
        this.isAuthenticated = false;

        this.login = (username, password) => {
            const api = axios.create()
            return (api.post('http://localhost:4000/login', { username, password }))

            //return res;
        }

        this.logout = () => {

        }
    }
}

export default new Auth();
