import axios from 'axios';

import store from '../../reduxState/store';

class Auth {
    constructor() {
        this.login = async (username, password) => {
            const api = axios.create()
            let res = await api.post('http://localhost:4000/login', { username, password })
            return res;
        }

        this.logout = (callback) => {
            localStorage.removeItem('authorization');
            callback();
        }

        this.save = async (token) => {
            const api = axios.create({ headers: { authorization: token } })
            let response = await api.post('http://127.0.0.1:4000/save',
                {
                    web: store.getState().web
                })
            return response;
        }
    }
}

export default new Auth();
